import React, { useState, useEffect, useContext } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  Paper,
  MenuItem
} from '@mui/material';
import { AlertContext } from '../context/alertContext';
import api from '../utils/axios';
import { useNavigate } from 'react-router-dom';

const NewOrder = () => {
  const [form, setForm] = useState({
    serviceId: '',
    quantity: 1,
    address: '',
    pickupDate: ''
  });

  const [services, setServices] = useState([]);
  const { showAlert } = useContext(AlertContext);
  const navigate = useNavigate();

  // ✅ Fetch services from backend
  useEffect(() => {
    const fetchServices = async () => {
      try {
        const res = await api.get('/services');

        if (Array.isArray(res.data.services)) {
          setServices(res.data.services);
        } else if (Array.isArray(res.data)) {
          setServices(res.data);
        } else {
          showAlert('No services found.', 'warning');
          setServices([]);
        }
      } catch (err) {
        console.error('Error fetching services:', err);
        showAlert('Failed to load services', 'error');
        setServices([]);
      }
    };

    fetchServices();
  }, []); // ❌ Do not include showAlert in dependency array here

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { serviceId, quantity, address, pickupDate } = form;

    if (!serviceId || !quantity || !address || !pickupDate) {
      showAlert('All fields are required.', 'error');
      return;
    }

    try {
      await api.post('/orders', form, { withCredentials: true });
      showAlert('Order placed successfully!', 'success');
      navigate('/dashboard');
    } catch (err) {
      console.error('Order error:', err);
      const msg = err?.response?.data?.error || 'Order placement failed';
      showAlert(msg, 'error');
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ p: 4, mt: 8 }}>
        <Typography variant="h5" gutterBottom>
          Place a New Order
        </Typography>

        <form onSubmit={handleSubmit}>
          <TextField
            select
            label="Select Service"
            name="serviceId"
            value={form.serviceId}
            onChange={handleChange}
            fullWidth
            required
            sx={{ mb: 2 }}
          >
            {services.length > 0 ? (
              services.map((service) => (
                <MenuItem key={service._id} value={service._id}>
                  {service.name} - ₹{service.price}
                </MenuItem>
              ))
            ) : (
              <MenuItem disabled>No services available</MenuItem>
            )}
          </TextField>

          <TextField
            label="Quantity"
            name="quantity"
            type="number"
            value={form.quantity}
            onChange={handleChange}
            fullWidth
            required
            inputProps={{ min: 1 }}
            sx={{ mb: 2 }}
          />

          <TextField
            label="Pickup Address"
            name="address"
            value={form.address}
            onChange={handleChange}
            fullWidth
            required
            sx={{ mb: 2 }}
          />

          <TextField
            label="Pickup Date"
            name="pickupDate"
            type="date"
            value={form.pickupDate}
            onChange={handleChange}
            fullWidth
            required
            InputLabelProps={{ shrink: true }}
            sx={{ mb: 3 }}
          />

          <Button type="submit" variant="contained" fullWidth>
            Place Order
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

export default NewOrder;
