import React from 'react';
import { Box, Button, Container, Typography, Grid, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/authContext';

const HeroSection = () => {
  const navigate = useNavigate();
  const { user } = useAuth();

  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      <Grid container spacing={4} alignItems="center">
        {/* Left Side: Text Content and Call to Action */}
        <Grid item xs={12} md={6}>
          <Typography
            variant="h2"
            component="h1"
            gutterBottom
            sx={{ fontWeight: 700, color: 'var(--text-primary)' }}
          >
            Fresh Clothes,
            <br />
            <span style={{ color: 'var(--accent-primary)' }}>Fresh Life.</span>
          </Typography>
          <Typography variant="h6" paragraph sx={{ color: 'var(--text-secondary)', my: 3 }}>
            Effortless laundry and dry cleaning services with free pickup and delivery. Schedule your order in seconds and enjoy a hassle-free experience.
          </Typography>
          <Button
            variant="contained"
            size="large"
            sx={{
              backgroundColor: 'var(--accent-primary)',
              py: 1.5,
              px: 4,
              fontSize: '1.1rem',
              '&:hover': { backgroundColor: 'var(--accent-secondary)' },
            }}
            onClick={() => navigate(user ? '/order/new' : '/register')}
          >
            Order Now
          </Button>
        </Grid>

        {/* Right Side: Illustration */}
        <Grid item xs={12} md={6}>
          <Box
            component="img"
            src="/images/laundry_illustration.jpg" // ✅ Image source updated to use .jpg
            alt="Laundry service illustration"
            sx={{
              maxWidth: '100%',
              height: 'auto',
            }}
          />
        </Grid>
      </Grid>
    </Container>
  );
};

const FeaturesSection = () => {
    const features = [
      { title: 'Convenient Pickup & Delivery', description: 'Schedule your laundry online and we’ll handle the rest.' },
      { title: 'Professional Cleaning', description: 'Our expert team ensures your clothes are cleaned to the highest standards.' },
      { title: 'Affordable Pricing', description: 'Get premium laundry services without breaking the bank.' },
    ];

  return (
    <Box sx={{ bgcolor: 'var(--bg-secondary)', py: 6 }}>
        <Container maxWidth="md">
            <Typography variant="h4" align="center" gutterBottom sx={{ fontWeight: 600 }}>
                How It Works
            </Typography>
            <Grid container spacing={4} sx={{ mt: 3 }}>
                {features.map((feature, index) => (
                    <Grid item xs={12} md={4} key={index}>
                        <Paper elevation={0} sx={{ p: 3, textAlign: 'center', bgcolor: 'transparent' }}>
                            <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>{feature.title}</Typography>
                            <Typography variant="body1" sx={{ color: 'var(--text-secondary)' }}>{feature.description}</Typography>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        </Container>
    </Box>
  );
};


// The main Home component now assembles the sections
const Home = () => (
  <Box>
    <HeroSection />
    <FeaturesSection />
  </Box>
);

export default Home;
