import React from 'react';
import { createRoot } from 'react-dom/client';
import App from './App';

// ✅ Import Day.js configuration (extends plugins globally)
import './utils/dayjsConfig';
// frontend/src/utils/dayjsConfig.js
import dayjs from 'dayjs';
import weekOfYear from 'dayjs/plugin/weekOfYear';
import localizedFormat from 'dayjs/plugin/localizedFormat';
import isBetween from 'dayjs/plugin/isBetween';

dayjs.extend(weekOfYear);
dayjs.extend(localizedFormat);
dayjs.extend(isBetween);

// Optional: set default locale or timezone here
// import utc from 'dayjs/plugin/utc';
// import timezone from 'dayjs/plugin/timezone';
// dayjs.extend(utc);
// dayjs.extend(timezone);
// dayjs.tz.setDefault('Asia/Kolkata');


// ErrorBoundary to catch unhandled errors
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error('Uncaught error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div style={{ padding: '20px', fontFamily: 'Roboto, sans-serif' }}>
          <h2 style={{ color: '#d32f2f' }}>Something went wrong</h2>
          <p>Please refresh the page or try again later</p>
          <button 
            onClick={() => window.location.reload()}
            style={{
              padding: '8px 16px',
              background: '#3f51b5',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Refresh Page
          </button>
        </div>
      );
    }

    return this.props.children;
  }
}

// Find and validate the root DOM container
const container = document.getElementById('root');

if (!container) {
  console.error('Failed to find the root element');
  document.body.innerHTML = `
    <div style="padding: 20px; color: red; font-family: sans-serif">
      <h2>Critical Error</h2>
      <p>Could not find root element to mount the application</p>
    </div>
  `;
} else {
  const root = createRoot(container);
  root.render(
    <React.StrictMode>
      <ErrorBoundary>
        <App />
      </ErrorBoundary>
    </React.StrictMode>
  );
}

// Optional service worker for production
if ('serviceWorker' in navigator && process.env.NODE_ENV === 'production') {
  window.addEventListener('load', () => {
    navigator.serviceWorker
      .register('/service-worker.js')
      .then(() => console.log('ServiceWorker registered successfully'))
      .catch(error => console.log('ServiceWorker registration failed:', error));
  });
}
