import React from 'react';
import {
  Box,
  Button,
  Container,
  Typography,
  Grid,
  Paper,
  useTheme,
  useMediaQuery
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const features = [
    { title: 'Fast Turnaround', text: 'Get clothes back in 24 hours' },
    { title: 'Eco-Friendly', text: 'Green cleaning solutions' },
    { title: 'Premium Service', text: 'Professional garment care' }
  ];

  return (
    <Box
      sx={{
        background: 'linear-gradient(to right, #121212, #1f1f1f)',
        minHeight: '100vh',
        py: 8,
        color: '#e0e0e0'
      }}
    >
      <Container maxWidth="md">
        {/* Hero Section */}
        <Box sx={{ textAlign: 'center', mb: 10 }}>
          <Typography
            variant={isMobile ? 'h4' : 'h2'}
            gutterBottom
            sx={{ fontWeight: 700, color: '#ffffff' }}
          >
            Welcome to LaundryPro
          </Typography>
          <Typography
            variant="h6"
            paragraph
            sx={{ maxWidth: 600, mx: 'auto', color: '#b0b0b0' }}
          >
            Premium laundry & dry-cleaning services delivered to your doorstep â€” fast, clean, and eco-conscious.
          </Typography>
          <Box sx={{ mt: 4 }}>
            <Button
              variant="contained"
              size="large"
              sx={{
                mr: 2,
                px: 4,
                py: 1.5,
                borderRadius: '8px',
                boxShadow: 3,
                backgroundColor: '#1976d2',
                '&:hover': { backgroundColor: '#1565c0' }
              }}
              onClick={() => navigate('/order/new')}
            >
              Place New Order
            </Button>
            <Button
              variant="outlined"
              size="large"
              sx={{
                px: 4,
                py: 1.5,
                borderRadius: '8px',
                color: '#90caf9',
                borderColor: '#90caf9',
                '&:hover': {
                  backgroundColor: '#222',
                  borderColor: '#bbdefb'
                }
              }}
              onClick={() => navigate('/track')}
            >
              Track Order
            </Button>
          </Box>
        </Box>

        {/* Features Section */}
        <Grid container spacing={4} justifyContent="center">
          {features.map((item, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Paper
                elevation={4}
                sx={{
                  p: 4,
                  textAlign: 'center',
                  borderRadius: 3,
                  backgroundColor: '#2c2c2c',
                  color: '#e0e0e0',
                  transition: '0.3s',
                  '&:hover': {
                    transform: 'translateY(-5px)',
                    boxShadow: 6
                  }
                }}
              >
                <Typography
                  variant="h6"
                  gutterBottom
                  sx={{ fontWeight: 600 }}
                >
                  {item.title}
                </Typography>
                <Typography variant="body2" color="#aaa">
                  {item.text}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
