// frontend/src/components/BottomNav.js
import React from 'react';
import { BottomNavigation, BottomNavigationAction, Paper } from '@mui/material';
import { Home, Login, ShoppingCart, PersonAdd } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

export default function BottomNav() {
  const [value, setValue] = React.useState(0);
  const navigate = useNavigate();

  const handleChange = (event, newValue) => {
    setValue(newValue);

    // Navigate based on selected tab
    switch (newValue) {
      case 0:
        navigate('/');
        break;
      case 1:
        navigate('/login');
        break;
      case 2:
        navigate('/register');
        break;
      case 3:
        navigate('/order/new');
        break;
      default:
        break;
    }
  };

  return (
    <Paper
      sx={{
        position: 'fixed',
        bottom: 0,
        left: 0,
        right: 0,
        zIndex: 1000,
      }}
      elevation={3}
    >
      <BottomNavigation value={value} onChange={handleChange} showLabels>
        <BottomNavigationAction label="Home" icon={<Home />} />
        <BottomNavigationAction label="Login" icon={<Login />} />
        <BottomNavigationAction label="Register" icon={<PersonAdd />} />
        <BottomNavigationAction label="Order" icon={<ShoppingCart />} />
      </BottomNavigation>
    </Paper>
  );
}
